// src\components\pages\views\Vol_Data.jsx
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Vol_Data() {
  const [srno, setSrno] = useState('');
  const [volunteerData, setVolunteerData] = useState(null);
  const [eventsByCategory, setEventsByCategory] = useState(new Map());
  let grandTotalHours = 0;

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/get-volunteer', { srno });
      setVolunteerData(response.data);
    } catch (error) {
      console.error('Error fetching volunteer data:', error);
    }
  };

  useEffect(() => {
    // Function to fetch volunteer data when srno changes
    const fetchVolunteerData = async () => {
      try {
        const response = await axios.post('/get-volunteer', { srno });
        setVolunteerData(response.data);
      } catch (error) {
        console.error('Error fetching volunteer data:', error);
      }
    };

    // Call the function to fetch data when srno changes
    if (srno) {
      fetchVolunteerData();
    }
  }, [srno]);

  return (
    <div>
      <h1 id="title">Volunteer Information</h1>
      <form className="form" onSubmit={handleFormSubmit}>
        <label htmlFor="srno">Enter Serial Number:</label>
        <input
          type="text"
          id="srno"
          name="srno"
          required
          value={srno}
          onChange={(e) => setSrno(e.target.value)}
        />
        <button type="submit" id="btn">Submit</button>
      </form>

      {volunteerData && (
        <div>
          <table border="1">
            <thead>
              <tr>
                <th>Serial Number</th>
                <th>Name</th>
                <th>VEC Number</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{volunteerData.srno}</td>
                <td>{volunteerData.name}</td>
                <td>{volunteerData.vecno}</td>
              </tr>
            </tbody>
          </table>

          {/* Initialize total hours variables */}
          {Array.from(eventsByCategory).map(([category, events]) => (
            <div key={category}>
              <h2>{category}</h2>
              <table border="1">
                <thead>
                  <tr>
                    <th>Event Name</th>
                    <th>Date</th>
                    <th>Hours Allotted</th>
                  </tr>
                </thead>
                <tbody>
                  {events.map((event) => {
                    const date = event.date
                      ? new Date(event.date).toLocaleDateString()
                      : 'No Date Available';

                    const hoursAllotted = parseInt(event.hoursalloted, 10) || 0;

                    // Calculate the total hours allotted for this category
                    grandTotalHours += hoursAllotted;

                    return (
                      <tr key={event.eventname}>
                        <td>{event.eventname}</td>
                        <td>{date}</td>
                        <td>{hoursAllotted}</td>
                      </tr>
                    );
                  })}

                  {/* Calculate the total hours allotted for this category */}
                  <tr>
                    <td colSpan="2">
                      <strong>Total</strong>
                    </td>
                    <td>{grandTotalHours}</td>
                  </tr>
                </tbody>
              </table>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default Vol_Data;
