// src\components\pages\views\Vol_Data.jsx
import React, { useState } from 'react';
import axios from 'axios';

function Vol_Data() {
  const [volunteerData, setVolunteerData] = useState(null);
  let grandTotalHours = 0; // Declare the variable outside JSX

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('http:127.0.0.1:3000/vol/get-volunteer', {
        srno: e.target.srno.value,
      });

      setVolunteerData(response.data.volunteerData);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1 id="title">Volunteer Information</h1>

      <form className="form" onSubmit={handleSubmit}>
        <label htmlFor="srno">Enter Serial Number:</label>
        <input type="text" id="srno" name="srno" required />
        <button type="submit" id="btn">
          Submit
        </button>
      </form>

      {volunteerData && (
        <div>
          <h2>Volunteer Details</h2>
          <table border="1">
            <tr>
              <th>Serial Number</th>
              <th>Name</th>
              <th>VEC Number</th>
            </tr>
            <tr>
              <td>{volunteerData.srno}</td>
              <td>{volunteerData.name}</td>
              <td>{volunteerData.vecno}</td>
            </tr>
          </table>

          {/* Initialize total hours variables */}
          {/* Iterate through categories and display tables */}
          {eventsByCategory.map((events, category) => (
            <div key={category}>
              <h2>{category}</h2>
              <table border="1">
                <thead>
                  <tr>
                    <th>Event Name</th>
                    <th>Date</th>
                    <th>Hours Allotted</th>
                  </tr>
                </thead>
                <tbody>
                  {events.map((event, index) => (
                    <tr key={index}>
                      <td>{event.eventname}</td>
                      <td>
                        {event.date
                          ? new Date(event.date).toLocaleDateString()
                          : 'No Date Available'}
                      </td>
                      <td>{event.hoursalloted}</td>
                    </tr>
                  ))}
                </tbody>
                {/* Calculate the total hours allotted for this category */}
                <tfoot>
                  <tr>
                    <td colSpan="2">
                      <strong>Total</strong>
                    </td>
                    <td>
                      {events.reduce(
                        (total, event) =>
                          total + parseFloat(event.hoursalloted) || 0,
                        0
                      )}
                    </td>
                  </tr>
                </tfoot>
              </table>
              {/* Add to the grand total hours */}
              {grandTotalHours += events.reduce(
                (total, event) => total + parseFloat(event.hoursalloted) || 0,
                0
              )}
            </div>
          ))}

          <br />
          <table border="1">
            {/* Display the grand total of all hours allotted */}
            <tr>
              <th colSpan="2">
                <strong> Grand Total </strong>
              </th>
              <td>{grandTotalHours}</td>
            </tr>
          </table>
        </div>
      )}
    </div>
  );
}

export default Vol_Data;
