import React, { useState } from 'react';
import axios from "axios";

function Vol_Data() {
  const [srno, setSrno] = useState("");
  const [volunteerData, setVolunteerData] = useState(null);

  const handleSearch = async () => {
    try {
      const response = await axios.post("http://localhost:3001/get-volunteer", {
        srno: srno,
      });
      setVolunteerData(response.data);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>

      <h1 id="title">Volunteer Information</h1>

      {/* User input form */}
      <form className="form" onSubmit={handleSubmit}>
        <label htmlFor="srno" id="srno">
          Enter Serial Number:
        </label>
        <input
          type="text"
          id="srno"
          name='srno'
          placeholder="Enter SRNO"
          value={srno}
          onChange={(e) => setSrno(e.target.value)}
          required
        />
        {/* <input type="text" id="srno" name="srno" required /> */}
        <button type="submit" id="btn" onClick={handleSearch}>
          Submit
        </button>
      </form>

      {/* Display volunteer information in a table */}
      {volunteerData && (
        <div>
          <table border="1">
            <thead>
              <tr>
                <th>Serial Number</th>
                <th>Name</th>
                <th>VEC Number</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{volunteerData.srno}</td>
                <td>{volunteerData.name}</td>
                <td>{volunteerData.vecno}</td>
              </tr>
            </tbody>
          </table>

          {/* Initialize total hours variables outside the return statement */}
          {grandTotalHours = 0}

          {/* Iterate through categories and display tables */}
          {volunteerData.eventsByCategory.map(({ category, events }) => (
            <div key={category}>
              <h2>{category}</h2>
              <table border="1">
                <thead>
                  <tr>
                    <th>Event Name</th>
                    <th>Date</th>
                    <th>Hours Allotted</th>
                  </tr>
                </thead>
                <tbody>
                  {events.map((event) => {
                    const date = event.date
                      ? new Date(event.date).toLocaleDateString()
                      : 'No Date Available';
                    const hoursAllotted = parseInt(event.hoursalloted, 10) || 0;
                    grandTotalHours += hoursAllotted;
                    return (
                      <tr key={event.eventname}>
                        <td>{event.eventname}</td>
                        <td>{date}</td>
                        <td>{hoursAllotted}</td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>

              {/* Calculate the total hours allotted for this category */}
              <div>
                <strong>Total:</strong> {grandTotalHours}
              </div>
            </div>
          ))}

          {/* Display the grand total of all hours allotted */}
          <table border="1">
            <thead>
              <tr>
                <th colSpan="2">
                  <strong>Grand Total</strong>
                </th>
                <td>{grandTotalHours}</td>
              </tr>
            </thead>
          </table>
        </div>
      )}
    </div>
  );
}

export default Vol_Data;
