import React, { useState } from 'react';

function Vol_Data() {
  // Define a state variable for volunteer data
  const [volunteerData, setVolunteerData] = useState(null);

  // Initialize total hours variables outside the return statement
  let grandTotalHours = 0;

  // Define a function to handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Make an API request to fetch volunteer data based on the entered serial number
    try {
      const response = await fetch('http://localhost:3000/get-volunteer', {
        method: 'POST',
        mode: 'cors',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ srno: srno.body }),
      });

      if (response.ok) {
        const data = await response.json();
        setVolunteerData(data); // Update the state with the fetched data
      } else {
        setVolunteerData(null); // Clear the data if not found
      }
    } catch (error) {
      console.error('Error fetching volunteer data:', error);
    }
  };

  return (
    <div>

      <h1 id="title">Volunteer Information</h1>

      {/* User input form */}
      <form className="form" onSubmit={handleSubmit}>
        <label htmlFor="srno" id="srno">
          Enter Serial Number:
        </label>
        <input type="text" id="srno" name="srno" required />
        <button type="submit" id="btn">
          Submit
        </button>
      </form>

      {/* Display volunteer information in a table */}
      {volunteerData && (
        <div>
          <table border="1">
            <thead>
              <tr>
                <th>Serial Number</th>
                <th>Name</th>
                <th>VEC Number</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{volunteerData.srno}</td>
                <td>{volunteerData.name}</td>
                <td>{volunteerData.vecno}</td>
              </tr>
            </tbody>
          </table>

          {/* Initialize total hours variables outside the return statement */}
          {grandTotalHours = 0}

          {/* Iterate through categories and display tables */}
          {volunteerData.eventsByCategory.map(({ category, events }) => (
            <div key={category}>
              <h2>{category}</h2>
              <table border="1">
                <thead>
                  <tr>
                    <th>Event Name</th>
                    <th>Date</th>
                    <th>Hours Allotted</th>
                  </tr>
                </thead>
                <tbody>
                  {events.map((event) => {
                    const date = event.date
                      ? new Date(event.date).toLocaleDateString()
                      : 'No Date Available';
                    const hoursAllotted = parseInt(event.hoursalloted, 10) || 0;
                    grandTotalHours += hoursAllotted;
                    return (
                      <tr key={event.eventname}>
                        <td>{event.eventname}</td>
                        <td>{date}</td>
                        <td>{hoursAllotted}</td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>

              {/* Calculate the total hours allotted for this category */}
              <div>
                <strong>Total:</strong> {grandTotalHours}
              </div>
            </div>
          ))}

          {/* Display the grand total of all hours allotted */}
          <table border="1">
            <thead>
              <tr>
                <th colSpan="2">
                  <strong>Grand Total</strong>
                </th>
                <td>{grandTotalHours}</td>
              </tr>
            </thead>
          </table>
        </div>
      )}
    </div>
  );
}

export default Vol_Data;
