// src\components\pages\views\Vol_Data.jsx
import React, { useEffect, useState } from 'react';

function Vol_Data() {
  const [volunteerData, setVolunteerData] = useState(null);
  let grandTotalHours = 0; // Declare the variable outside JSX

  const fetchData = async () => {
    try {
      const response = await fetch('/api/data');
      const jsonData = await response.json();
      setData(jsonData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/get-volunteer', {
        method: 'POST',
        mode: 'cors', 
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ srno: <e className="target srno any"></e> }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setVolunteerData(data.volunteerData);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1 id="title">Volunteer Information</h1>

      <form className="form" onSubmit={handleSubmit}>
        <label htmlFor="srno">Enter Serial Number:</label>
        <input type="text" id="srno" name="srno" value={srno}
          onChange={(e) => setName(e.target.value)} required />
        <button type="submit" id="btn">
          Submit
        </button>
      </form>

      {volunteerData && (
        <div>
          <h2>Volunteer Details</h2>
          <table border="1">
            <thead>
              <tr>
                <th>Serial Number</th>
                <th>Name</th>
                <th>VEC Number</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{volunteerData.srno}</td>
                <td>{volunteerData.name}</td>
                <td>{volunteerData.vecno}</td>
              </tr>
            </tbody>
          </table>

          {/* Initialize total hours variables */}
          {/* Iterate through categories and display tables */}
          {eventsByCategory.map((events, category) => (
            <div key={category}>
              <h2>{category}</h2>
              <table border="1">
                <thead>
                  <tr>
                    <th>Event Name</th>
                    <th>Date</th>
                    <th>Hours Allotted</th>
                  </tr>
                </thead>
                <tbody>
                  {events.map((event, index) => (
                    <tr key={index}>
                      <td>{event.eventname}</td>
                      <td>
                        {event.date
                          ? new Date(event.date).toLocaleDateString()
                          : 'No Date Available'}
                      </td>
                      <td>{event.hoursalloted}</td>
                    </tr>
                  ))}
                </tbody>
                {/* Calculate the total hours allotted for this category */}
                <tfoot>
                  <tr>
                    <td colSpan="2">
                      <strong>Total</strong>
                    </td>
                    <td>
                      {events.reduce(
                        (total, event) =>
                          total + parseFloat(event.hoursalloted) || 0,
                        0
                      )}
                    </td>
                  </tr>
                </tfoot>
              </table>
              {/* Add to the grand total hours */}
              {grandTotalHours += events.reduce(
                (total, event) => total + parseFloat(event.hoursalloted) || 0,
                0
              )}
            </div>
          ))}

          <br />
          <table border="1">
            {/* Display the grand total of all hours allotted */}
            <tbody>
              <tr>
                <td colSpan="2">
                  <strong> Grand Total </strong>
                </td>
                <td>{grandTotalHours}</td>
              </tr>
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
}

export default Vol_Data;
