// src\components\pages\views\Vol_Data.jsx
import React, { useEffect, useState } from 'react';

function Vol_Data() {
  const [volunteerData, setVolunteerData] = useState(null);
  const [srno, setSrno] = useState('');
  const [grandTotalHours, setGrandTotalHours] = useState(0);
  const [eventsByCategory, setEventsByCategory] = useState([]);

  const fetchData = async () => {
    try {
      const response = await fetch('/get-volunteer');
      const jsonData = await response.json();
      setVolunteerData(jsonData.volunteerData);
      setEventsByCategory(jsonData.eventsByCategory);
      setGrandTotalHours(jsonData.grandTotalHours);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/get-volunteer', {
        method: 'POST',
        mode: 'cors',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ srno: e.target.srno.value }),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setVolunteerData(data.volunteerData);
      setEventsByCategory(data.eventsByCategory);
      setGrandTotalHours(data.grandTotalHours);
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div>
      <h1 id="title">Volunteer Information</h1>

      <form className="form" onSubmit={handleSubmit}>
        <label htmlFor="srno">Enter Serial Number:</label>
        <input
          type="text"
          id="srno"
          name="srno"
          value={srno}
          onChange={(e) => setSrno(e.target.value)}
          required
        />
        <button type="submit" id="btn">
          Submit
        </button>
      </form>

      {volunteerData && (
        <div>
          <h2>Volunteer Details</h2>
          <table border="1">
            <thead>
              <tr>
                <th>Serial Number</th>
                <th>Name</th>
                <th>VEC Number</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{volunteerData.srno}</td>
                <td>{volunteerData.name}</td>
                <td>{volunteerData.vecno}</td>
              </tr>
            </tbody>
          </table>

          {eventsByCategory.map((categoryData, index) => (
            <div key={index}>
              <h2>{categoryData.category}</h2>
              <table border="1">
                <thead>
                  <tr>
                    <th>Event Name</th>
                    <th>Date</th>
                    <th>Hours Allotted</th>
                  </tr>
                </thead>
                <tbody>
                  {categoryData.events.map((event, eventIndex) => (
                    <tr key={eventIndex}>
                      <td>{event.eventname}</td>
                      <td>
                        {event.date
                          ? new Date(event.date).toLocaleDateString()
                          : 'No Date Available'}
                      </td>
                      <td>{event.hoursalloted}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
              <p>Total Hours for {categoryData.category}: {categoryData.totalHoursCategory}</p>
            </div>
          ))}

          <br />
          <p>Grand Total Hours: {grandTotalHours}</p>
        </div>
      )}
    </div>
  );
}

export default Vol_Data;
